#' Detrended Fluctuation Analysis
#'
#' 
#' @param data .
#' @param y1 .
#' @param standardise .
#' @param sumORDER .
#' @param removeTrend .
#' @param poly_order .
#' @param max_order .
#' @param minScale .
#' @param userMinScale .
#' @param maxScale .
#' @param userMaxScale .
#' @param scaleRES .
#' @param scaleExclude .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$tblTS} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tblDFA} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tsplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$dfaplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$tblTS$asDF}
#'
#' \code{as.data.frame(results$tblTS)}
#'
#' @export
faDFA <- function(
    data,
    y1,
    standardise = "meanSD",
    sumORDER = FALSE,
    removeTrend = "poly",
    poly_order = 1,
    max_order = 5,
    minScale = "autoMin",
    userMinScale = 2,
    maxScale = "autoMax",
    userMaxScale = 4,
    scaleRES = 30,
    scaleExclude = 4) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("faDFA requires jmvcore to be installed (restart may be required)")

    if ( ! missing(y1)) y1 <- jmvcore::resolveQuo(jmvcore::enquo(y1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(y1), y1, NULL))


    options <- faDFAOptions$new(
        y1 = y1,
        standardise = standardise,
        sumORDER = sumORDER,
        removeTrend = removeTrend,
        poly_order = poly_order,
        max_order = max_order,
        minScale = minScale,
        userMinScale = userMinScale,
        maxScale = maxScale,
        userMaxScale = userMaxScale,
        scaleRES = scaleRES,
        scaleExclude = scaleExclude)

    analysis <- faDFAClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

