#' \code{as.data.frame(results$tblTS)}
#'
#' @export
recNet <- function(
    data,
    y1,
    standardise = "none",
    edgeWeight = "none",
    Glayout,
    LayoutA = 0,
    LayoutB = 0,
    Narcs = 4,
    Glabels = TRUE,
    nsize = "degree",
    PruneDegree = 0,
    PruneWeight = 0,
    emLag = 1,
    emDim = 1,
    fixRR = 0.05,
    fixRAD = 0,
    fixed = "RAD",
    DLmin = 2,
    DLmax = 0,
    VLmin = 2,
    VLmax = 0,
    HLmin = 2,
    HLmax = 0,
    theiler = 0,
    norm = "euclidean") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("recNet requires jmvcore to be installed (restart may be required)")

    if ( ! missing(y1)) y1 <- jmvcore::resolveQuo(jmvcore::enquo(y1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(y1), y1, NULL))


    options <- recNetOptions$new(
        y1 = y1,
        standardise = standardise,
        edgeWeight = edgeWeight,
        Glayout = Glayout,
        LayoutA = LayoutA,
        LayoutB = LayoutB,
        Narcs = Narcs,
        Glabels = Glabels,
        nsize = nsize,
        PruneDegree = PruneDegree,
        PruneWeight = PruneWeight,
        emLag = emLag,
        emDim = emDim,
        fixRR = fixRR,
        fixRAD = fixRAD,
        fixed = fixed,
        DLmin = DLmin,
        DLmax = DLmax,
        VLmin = VLmin,
        VLmax = VLmax,
        HLmin = HLmin,
        HLmax = HLmax,
        theiler = theiler,
        norm = norm)

    analysis <- recNetClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

