#' Auto RQA
#'
#' 
#' @param data .
#' @param y1 .
#' @param standardise .
#' @param emLag .
#' @param emDim .
#' @param fixRR .
#' @param fixRAD .
#' @param fixed .
#' @param DLmin .
#' @param DLmax .
#' @param VLmin .
#' @param VLmax .
#' @param HLmin .
#' @param HLmax .
#' @param theiler .
#' @param norm .
#' @param plotDP .
#' @param diagWin .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$tblTS} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rpplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$tblRP} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tblRQA} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$DPplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$warnings} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$tblTS$asDF}
#'
#' \code{as.data.frame(results$tblTS)}
#'
#' @export
rqaAU <- function(
    data,
    y1,
    standardise = "none",
    emLag = 1,
    emDim = 1,
    fixRR = 0.05,
    fixRAD = 0,
    fixed = "RAD",
    DLmin = 2,
    DLmax = 0,
    VLmin = 2,
    VLmax = 0,
    HLmin = 2,
    HLmax = 0,
    theiler = 0,
    norm = "euclidean",
    plotDP = FALSE,
    diagWin = 10) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rqaAU requires jmvcore to be installed (restart may be required)")

    if ( ! missing(y1)) y1 <- jmvcore::resolveQuo(jmvcore::enquo(y1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(y1), y1, NULL))


    options <- rqaAUOptions$new(
        y1 = y1,
        standardise = standardise,
        emLag = emLag,
        emDim = emDim,
        fixRR = fixRR,
        fixRAD = fixRAD,
        fixed = fixed,
        DLmin = DLmin,
        DLmax = DLmax,
        VLmin = VLmin,
        VLmax = VLmax,
        HLmin = HLmin,
        HLmax = HLmax,
        theiler = theiler,
        norm = norm,
        plotDP = plotDP,
        diagWin = diagWin)

    analysis <- rqaAUClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

