#' Entropy & Roughness
#'
#' 
#' @param data .
#' @param y1 .
#' @param Ent_edim .
#' @param Ent_r .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$tsdesc} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$tsdesc$asDF}
#'
#' \code{as.data.frame(results$tsdesc)}
#'
#' @export
tsENT <- function(
    data,
    y1,
    Ent_edim = 3,
    Ent_r = 1) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("tsENT requires jmvcore to be installed (restart may be required)")

    if ( ! missing(y1)) y1 <- jmvcore::resolveQuo(jmvcore::enquo(y1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(y1), y1, NULL))


    options <- tsENTOptions$new(
        y1 = y1,
        Ent_edim = Ent_edim,
        Ent_r = Ent_r)

    analysis <- tsENTClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

