#' Generate random surrogates
#'
#' 
#' @param data .
#' @param y1 .
#' @param surmethod .
#' @param numSUR .
#' @param Save .
#' @param savePath .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$tsplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$surplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$savemessage} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
tsSUR <- function(
    data,
    y1,
    surmethod = "choose",
    numSUR = 1,
    Save = FALSE,
    savePath = "") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("tsSUR requires jmvcore to be installed (restart may be required)")

    if ( ! missing(y1)) y1 <- jmvcore::resolveQuo(jmvcore::enquo(y1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(y1), y1, NULL))


    options <- tsSUROptions$new(
        y1 = y1,
        surmethod = surmethod,
        numSUR = numSUR,
        Save = Save,
        savePath = savePath)

    analysis <- tsSURClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

