#' Statistical Tests of Data Assumptions
#'
#' 
#' @param data .
#' @param y1 .
#' @param jb .
#' @param bTRND .
#' @param bSOSC .
#' @param bVON .
#' @param adf .
#' @param kpssLVL .
#' @param kpssTRND .
#' @param csd .
#' @param csu .
#' @param csdu .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$jbOut} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$bTRNDout} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$bSOSCout} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$bVONout} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$adfOut} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$kpssLVLout} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$kpssTRNDout} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$csdOut} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$csuOut} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$csduOut} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
tsTST <- function(
    data,
    y1,
    jb = FALSE,
    bTRND = FALSE,
    bSOSC = FALSE,
    bVON = FALSE,
    adf = FALSE,
    kpssLVL = FALSE,
    kpssTRND = FALSE,
    csd = FALSE,
    csu = FALSE,
    csdu = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("tsTST requires jmvcore to be installed (restart may be required)")

    if ( ! missing(y1)) y1 <- jmvcore::resolveQuo(jmvcore::enquo(y1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(y1), y1, NULL))


    options <- tsTSTOptions$new(
        y1 = y1,
        jb = jb,
        bTRND = bTRND,
        bSOSC = bSOSC,
        bVON = bVON,
        adf = adf,
        kpssLVL = kpssLVL,
        kpssTRND = kpssTRND,
        csd = csd,
        csu = csu,
        csdu = csdu)

    analysis <- tsTSTClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

